'use strict';

/**
 * Module dependencies.
 */
const SkipSongEmbed = require('../embeds/skip-song-embed');
const ErrorEmbed = require('../embeds/error-embed');

/**
 * Skip command.
 */
const skip = {
	name: 'skip',
	description: 'Mevcut şarkıyı atla',
	execute(message, arg, musicBot) {
		const serverQueue = musicBot.queue.get(message.guild.id);

		if (!message.member.voiceChannel) {
			return message.channel.send(new ErrorEmbed('Şarkıyı durdurmak için bir ses kanalında olmalısın!'));
		}

		if (!serverQueue) {
			return message.channel.send(new ErrorEmbed('Ortada bir şarkı olmadığı için şarkı atlanamıyor!'));
		}

		message.channel.send(new SkipSongEmbed(serverQueue.songs[0]));
		serverQueue.connection.dispatcher.end();
	}
};

/**
 * Module exports.
 */
module.exports = skip;
