'use strict';

/**
 * Module dependencies.
 */
const StopQueueEmbed = require('../embeds/stop-queue-embed');
const ErrorEmbed = require('../embeds/error-embed');

/**
 * Stop command.
 */
const stop = {
	name: 'stop',
	description: 'Şarkıyı durdur ve sırayı temizle',
	execute(message, arg, musicBot) {
		const serverQueue = musicBot.queue.get(message.guild.id);

		if (!serverQueue) {
			return message.channel.send(new ErrorEmbed('Sırada şarkı yok moruq'));
		}

		if (!message.member.voiceChannel) {
			return message.channel.send(new ErrorEmbed('Müzik oynatmak için sesli sohbet kanalında olmalısın!'));
		}

		serverQueue.songs = [];
		serverQueue.connection.dispatcher.end();
		message.channel.send(new StopQueueEmbed());
	}
};

/**
 * Module exports.
 */
module.exports = stop;
