'use strict';

const DefaultEmbed = require('./default-embed');

/**
 * Add playlist embed.
 *
 * @class
 * @extends DefaultEmbed
 */
class AddPlaylistEmbed extends DefaultEmbed {
	constructor(playlist) {
		super(playlist);
		this.setTitle(':hourglass: Oynatma listesi sıraya eklendi');
		this.addField('Başlık', `\`${playlist.title}\``);
		this.addField('Sanatçı', `\`${playlist.author}\``);
		this.addField('Bağlantı', playlist.url);
		this.setImage(playlist.thumbnailUrl);
	}
}

module.exports = AddPlaylistEmbed;
