'use strict';

const DefaultEmbed = require('./default-embed');

/**
 * Play song embed.
 *
 * @class
 * @extends DefaultEmbed
 */
class PlaySongEmbed extends DefaultEmbed {
	constructor(song) {
		super();

		this.setTitle(':notes: Şuanda çalınıyor');
		this.addField('Başlık', `\`${song.title}\``);
		this.addField('Sanatçı', `\`${song.author}\``);
		this.addField('Süre', `\`${song.duration}\``);
		this.addField('Bağlantı', song.url);
		this.setImage(song.thumbnailUrl);
	}
}

module.exports = PlaySongEmbed;
